import React, { useState } from "react";
import "./Cart.css"; 
import { Trash, Plus, Minus, ChevronLeft } from "lucide-react";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const navigate = useNavigate();
  // Sample cart items (You can replace this with data from API)
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      name: "Wireless Headphones",
      price: 929,
      quantity: 1,
      image: "data:image/jpeg;base64,",
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 1499,
      quantity: 1,
      image: "data:image/jpeg;base64,",
    }
  ]);

  // Increase Quantity
  const increaseQuantity = (id) => {
    setCartItems((prev) =>
      prev.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  // Decrease Quantity
  const decreaseQuantity = (id) => {
    setCartItems((prev) =>
      prev
        .map((item) =>
          item.id === id && item.quantity > 1
            ? { ...item, quantity: item.quantity - 1 }
            : item
        )
        .filter((item) => item.quantity > 0) // Remove item if quantity is 0
    );
  };

  // Remove Item
  const removeItem = (id) => {
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  };

  // Calculate Total Price
  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
    <div className="app-bar">  
         <ChevronLeft className="left-icon " size={24} onClick={() => navigate(-1)} /> {/* Go Back */}
            Shopping Bag</div>
      {/* Delivery Section */}
      <div className="cart-delivery">
      <div className="box">
   Delivering to <strong>411005</strong> 
</div>
        <button className="change-btn">Change</button>
      </div>

      {/* Offers */}
      <div className="cart-offers">
        <p>Assured Cashback upto 399</p>
        <span>On transactions of 500 and above</span>
      </div>

      {/* Savings Info */}
      <div className="cart-savings">
        <span> You're saving <strong>390</strong> on this order</span>
      </div>

      {/* Cart Items */}
      {cartItems.length > 0 ? (
        cartItems.map((item) => (
          <div className="cart-item" key={item.id}>
            <img src={item.image} alt={item.name} className="product-image" />
            <div className="cart-details">
              <h3>{item.name}</h3>
              <p className="cart-price">{item.price * item.quantity}</p>
            </div>
            <div className="cart-actions">
              <button className="btn-icon" onClick={() => decreaseQuantity(item.id)}>
                <Minus size={16} />
              </button>
              <span className="quantity">{item.quantity}</span>
              <button className="btn-icon" onClick={() => increaseQuantity(item.id)}>
                <Plus size={16} />
              </button>
              <button className="btn-delete" onClick={() => removeItem(item.id)}>
                <Trash size={16} />
              </button>
            </div>
          </div>
        ))
      ) : (
        <p className="empty-cart">Your cart is empty!</p>
      )}

      {/* Order Summary */}
      {cartItems.length > 0 && (
        <div className="cart-footer">
          <p> {cartItems.length} Items Selected (<strong>{totalPrice}</strong>)</p>
          <button className="place-order">PLACE ORDER</button>
        </div>
      )}
      <div className="box2">

      </div>
    </div>
  );
};

export default Cart;